mapper.update:
UPDATE MEMBER
mbr_name = #{mbrName}, mbr_total_point = #{mbrTotalPoint}, mbr_total_exp = #{mbrTotalExp}, mbr_birthdate = #{mbrBirthdate}, mbr_height = #{mbrHeight}, mbr_weight = #{mbrWeight}, mbr_exercise_goal = #{mbrExerciseGoal}, mbr_exercise_experience_level = #{mbrExerciseExperienceLevel}, mbr_exercise_target = #{mbrExerciseTarget}, mbr_knee_health_considered = #{mbrKneeHealthConsidered}, mbr_noise_considered = #{mbrNoiseConsidered}, mbr_long_sitter = #{mbrLongSitter}, mbr_neck_shoulder_focused = #{mbrNeckShoulderFocused}, mbr_back_disk_considered = #{mbrBackDiskConsidered},
WHERE mbr_id = #{mbrId}

mapper.select:
SELECT COUNT(*) FROM MEMBER m
            JOIN PERSONAL_TRAINING pt ON m.MBR_SEQ = pt.MBR_SEQ AND m.MBR_SEQ = #{mbrSeq}
            WHERE pt.PT_RESERVATION_STATUS = 2 OR pt.PT_RESERVATION_STATUS = 3
